import { Component } from '@angular/core';
import { Cliente } from "./cliente";
import { ClienteService } from './cliente.service';
import Swal from 'sweetalert2';


@Component({
  selector: 'app-clientes',
  templateUrl: './clientes.component.html',
  styleUrls: ['./clientes.component.css']
})
export class ClientesComponent {

  clientes: Cliente[] ;

  constructor(private clienteService: ClienteService){}
  ngOnInit(){
   this.clienteService.getClientes().subscribe(
    clientes => this.clientes = clientes
   );
  }

  delete(cliente: Cliente): void{
    Swal.fire({
      title: 'Esta seguro?',
      text: `Seguro que desea eliminar el cliente ${cliente.nombre}? ${cliente.apellido}`,
      showDenyButton: true,
      showCancelButton: true,
      confirmButtonText: "Eliminar",
      denyButtonText: `Cancelar`,
    }).then((result) => {
      /* Read more about isConfirmed, isDenied below */
      if (result.isConfirmed) {

        this.clienteService.delete(cliente.id).subscribe(
          response =>{
            this.clientes = this.clientes.filter(cli => cli !== cliente)
            Swal.fire('Cliente eliminado!', `Cliente ${cliente.nombre} eliminado con exito`, 'success')
          }
        )


      } 
    })
  }

}
<div class="card border-primary mb-3">
    <div class="card-header">Clientes</div>
    <div class="card-body text-primary">
      <h5 class="card-title">Listado de clientes</h5>
      <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>

      <div class="my-2 text-left">
        <button class="btn btn-rounded btn-primary" type="button" [routerLink]="['/clientes/form']">Crear Cliente</button>
      </div>

    <table class="table table-bordered table-striped">
        <thead>
            <tr>
                <th>id</th>
                <th>nombre</th>
                <th>apellido</th>
                <th>email</th>
                <th>fecha</th>
                <th>Editar</th>
            </tr>

        </thead>
        <tbody>
            <tr *ngFor="let cliente of clientes">
                <td>{{cliente.id}}</td>
                <td>{{cliente.nombre}}</td>
                <td>{{cliente.apellido}}</td>
                <td>{{cliente.email}}</td>
                <td>{{cliente.createAt}}</td>

                <td>
                    <button type="button" name="editar" [routerLink]="['/clientes/form', cliente.id]" class="btn btn-primary">Editar</button>
                </td>
                <td>
                    <button type="button" name="eliminar" (click)='delete(cliente)' class="btn btn-danger">Eliminar</button>
                </td>
            </tr>
        </tbody>
    </table>

</div>
